import React, { Component } from "react";

import EmployeService from "../services/EmployeService";
export default class ViewEmployee extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.match.params.id,
      employee: {}
    };
  }
  componentDidMount() {
    EmployeService.getEmployeeById(this.state.id).then(res => {
      this.setState({ employee: res.data });
    });
  }
  render() {
    return (
      <div>
        <div className="card col-md-6 offset-3">
          <h3> View Employee Details</h3>
          <div className="card-body">
            <div className="row d-flex flex-column ">
              <div className="d-flex flex-row">
                <label> Employee First Name:</label>
                <div> {this.state.employee.first_name}</div>
              </div>
              <div className="d-flex flex-row">
                <label> Employee Last Name:</label>
                <div> {this.state.employee.last_name}</div>
              </div>
              <div>
                <label> Employee email address:</label>
                <div> {this.state.employee.email}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
