import React, { Component } from "react";
import EmployeService from "../services/EmployeService";

export default class CreateEmployee extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: this.props.match.params.id,
      firstname: "",
      lastname: "",
      email: ""
    };
    this.onchangefirstname = this.onchangefirstname.bind(this);
    this.onchangelastname = this.onchangelastname.bind(this);
    this.onchangeemail = this.onchangeemail.bind(this);
    this.CancelEmployee = this.CancelEmployee.bind(this);
    this.SaveEmployee = this.SaveEmployee.bind(this);
    this.SaveorupdateEmployee = this.SaveorupdateEmployee.bind(this);
    this.updateEmployee = this.updateEmployee.bind(this);
  }
  componentDidMount() {
    if (this.state.id === -1) {
      return;
    } else {
      EmployeService.getEmployeeById(this.state.id).then(res => {
        let employee = res.data;
        this.setState({
          firstname: employee.first_name,
          lastname: employee.last_name,
          email: employee.email
        });
      });
    }
  }
  onchangefirstname(event) {
    this.setState({ firstname: event.target.value });
  }

  onchangelastname(event) {
    this.setState({ lastname: event.target.value });
  }

  onchangeemail(event) {
    this.setState({ email: event.target.value });
  }

  SaveorupdateEmployee(e) {
    e.preventDefault();
    let employee = {
      first_name: this.state.firstname,
      last_name: this.state.lastname,
      email: this.state.email
    };
    if (this.state.id === "-1") {
      EmployeService.setEmployee(employee).then(res => {
        this.props.history.replace("/Employees");
      });
    } else {
      EmployeService.updateEmployee(employee).then(res => {
        this.props.history.replace("/Employees");
      });
      console.log(this.state.id);
    }
  }

  CancelEmployee() {
    this.props.history.replace("/Employees");
  }

  render() {
    return (
      <div>
        <div className="CreateEmployee container">
          <div className="row   mt-5">
            <div className="card col-md-6 offset-md-3">
              <h3>Add Employee</h3>
              <div className=" card-body">
                <form>
                  <div className="text-align-start form-group">
                    <label className=" ">First Name:</label>
                    <input
                      type="text"
                      placeholder="First Name"
                      name="firstName"
                      className="form-control"
                      value={this.props.firstname}
                      onChange={this.onchangefirstname}
                    />
                  </div>
                  <div className="form-group">
                    <label>Last Name:</label>
                    <input
                      type="text"
                      placeholder="Last Name"
                      name="lastName"
                      className="form-control"
                      value={this.props.lastname}
                      onChange={this.onchangelastname}
                    />
                  </div>
                  <div className="form-group">
                    <label>email:</label>
                    <input
                      type="email"
                      placeholder="email address"
                      name="email"
                      className="form-control"
                      value={this.props.email}
                      onChange={this.onchangeemail}
                    />
                  </div>
                </form>
              </div>
              <div className="row card-footer">
                <button
                  className="offset-sm-1 btn btn-primary "
                  onClick={this.CancelEmployee}
                >
                  Cancel Employee
                </button>
                <button
                  className="offset-sm-1 btn btn-primary "
                  onClick={this.SaveorupdateEmployee}
                >
                  save Employee
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
